mapper.select:
select order_package_id from ex_bus_order
where supplier_id = #{supplierId,jdbcType=VARCHAR} 
	and ticket_status = #{ticketStatus,jdbcType=VARCHAR} 
	and ticket_rec_status = #{ticketRecStatus,jdbcType=VARCHAR}
	and ticket_refund_status = #{ticketRefundStatus,jdbcType=VARCHAR}
	and IS_TEST_ORDER != 'Y'
	group by order_package_id

